content.query:
let T = datatable ( AlertLink:string)
[
{AlertLink}
];
T
| parse AlertLink with * '/alertId/' AlertId '/subscriptionId/' SubscriptionId '/resourceGroup/' ResourceGroup '/' * 'location/' Location 
| distinct AlertLink, AlertId, ResourceGroup,Location,SubscriptionId
| join kind = inner (SecurityAlert
| where isempty(ResourceId) == false
| where TimeGenerated > ago(90d)
| project SystemAlertId,ResourceId, DisplayName,StartTime) on $left.AlertId == $right.SystemAlertId
| project ResourceId,DisplayName,AlertId, SubscriptionId, ResourceGroup, Location,StartTime
| order by ResourceId,DisplayName, StartTime asc
